# Flattern JSON Data
def clt_pr_flatten_json(json_data):
    flattened_data = []

    for time_scale in ["annual", "monthly", "seasonal"]:
        for period in json_data['data'][time_scale]:
            for country in json_data['data'][time_scale][period]:
                for date, value in json_data['data'][time_scale][period][country].items():
                    flattened_data.append({
                        'time_scale': time_scale,
                        'period': period,
                        'country': country,
                        'date': date,
                        'clt_precipitation': value
                    })

    return flattened_data

# Parse the JSON string
# Climatology Dataset
def clt_create_df(data):
  for ele in data:
    clt_df = json.loads(data)
    clt_df = pd.DataFrame(clt_pr_flatten_json(clt_df))
  return clt_df

