# Heatplot Dataset
def hp_pr_df(json_data):
    data = json.loads(json_data)
    data = data['data']['BRA']
    df = pd.DataFrame(list(data.items()), columns=['date', 'heatplot_precipitation'])
    df = df.sort_values('date')
    df['date'] = pd.to_datetime(df['date'], format='%Y-%m')
    return df
