# Time-series Dataset
def ts_pr_df(json_data):
  data = json.loads(json_data)
  annual_data = data['data']['annual']['BRA']
  monthly_data = data['data']['monthly']['BRA']
  seasonal_data = data['data']['seasonal']['BRA']
  a_df = pd.DataFrame.from_dict(annual_data, orient='index', columns=['annual_time_series_precipitation'])
  m_df = pd.DataFrame.from_dict(monthly_data, orient='index', columns=['monthly_time_series_precipitation'])
  s_df = pd.DataFrame.from_dict(seasonal_data, orient='index', columns=['seasonal_time_series_precipitation'])
  a_df.reset_index(inplace=True)
  m_df.reset_index(inplace=True)
  s_df.reset_index(inplace=True)
  a_df.rename(columns={'index': 'date'}, inplace=True)
  m_df.rename(columns={'index': 'date'}, inplace=True)
  s_df.rename(columns={'index': 'date'}, inplace=True)
  return a_df, m_df, s_df

