# City-level Climatology Precipitation Datasets
def city_clt_df(json_data):
  data = json.loads(json_data)
  annual_data = data['data']
  df_data = {"subnational":[], "date":[], "annual_precipitation":[]}
  for region, values in annual_data.items():
    df_data["subnational"].append(region)
    for key, value in values.items():
      df_data["date"].append(key)
      df_data['annual_precipitation'].append(value)
  df = pd.DataFrame(df_data)
  return df

