# City-level Time Series Precipitation Datasets
def ts_pre_city_df(json_data):
  data = json.loads(json_data)
  pre_data = data['data']
  rows = []

  for region, values in pre_data.items():
    for date, value in values.items():
      rows.append({'index': region, 'date': date, 'precipitation': value})
  df = pd.DataFrame(rows)
  df_pivot = df.pivot(index='date', columns='index', values='precipitation')
  df_pivot = df_pivot.reset_index()
  return df_pivot

